    public static char charWithPinyinFirstLetter(char hz) {
        if(('a' <= hz && hz <= 'z') || ('A' <= hz && hz <= 'Z') ) return hz;
        int index = hz - N3HANZI_START;
        if (index >= 0 && index <= N3HANZI_COUNT) {
            return firstLetterArray.charAt(index);
        } else {
            return '#';
        }
    }

    public static String stringWithPinyinFirstLetter(String hzs) {
        StringBuffer sb = new StringBuffer();
        for (char hz : hzs.toCharArray()) {
            sb.append(charWithPinyinFirstLetter(hz));
        }
        return sb.toString();
    }

